<?php use PHPMailer\PHPMailer\PHPMailer;use PHPMailer\PHPMailer\Exception;header('Content-type: text/html; charset=UTF-8');setlocale(LC_TIME,'pt_BR','pt_BR.utf-8','pt_BR.utf-8','portuguese');date_default_timezone_set('America/Sao_Paulo');$email_send='contato@agenciapublikando.com.br';$name=filter_input(INPUT_POST,'nome');$msg=filter_input(INPUT_POST,'message');$contato=filter_input(INPUT_POST,'contato');$email=filter_input(INPUT_POST,'email',FILTER_VALIDATE_EMAIL);$body="Mensagem: <br>".$msg."<br>--<br> contato: ".$contato;$email_smtp='contato@agenciapublikando.com.br';$pass_smtp='cHVibGlrQG5kby4yMDIw';send_email($email_send,$email_smtp,$pass_smtp,"PUBLIKANDO - formulario",'Agencia publikando ',$body,$email,$name);function send_email($email_business,$email_smtp,$pass_smtp,$subject,$title,$body,$email_usu,$nome_usu){header('Content-Type: text/html; charset=utf-8');date_default_timezone_set("Brazil/East");if(PHP_VERSION_ID<50600){iconv_set_encoding('input_encoding','UTF-8');iconv_set_encoding('output_encoding','UTF-8');iconv_set_encoding('internal_encoding','UTF-8');}else{ini_set('default_charset','UTF-8');}require 'Exception.php';require 'PHPMailer.php';require 'SMTP.php';$mail=new PHPMailer(TRUE);$message=$body."<br> Nome: ".$nome_usu."<br>Email: ".$email_usu;try{$mail->setFrom($email_business,'formulario - Publikando');$mail->addAddress($email_business,'Nos');$subject=utf8_decode($subject);$mail->Subject=$subject;$mail->isHTML(TRUE);$mail->Body=$message;$mail->isSMTP();$mail->SMTPDebug=false;$mail->Debugoutput='html';$mail->Host='mail.agenciapublikando.com.br';$mail->Port=587;$mail->SMTPSecure='tls';$mail->SMTPAuth=true;$mail->Username=$email_smtp;$mail->Password=base64_decode($pass_smtp);$mail->send();}catch(Exception $e){$headers=array("MIME-Version: 1.1","From: ".$email_business."","Content-type: text/html; charset=iso-8859-1","Reply-To: The Sender <".$email_business.">","Return-Path: The Sender <".$email_business.">","Organization: Sender Organization","X-Priority: 3","X-Mailer: PHP".phpversion());$headers=implode("\r\n",$headers);try{mail($email_usu,$subject,$message,$headers);}catch(Exception $e){echo $e->errorMessage();}}}